import React, { Component } from 'react'

export class NewsItems extends Component {
  render() {
      let {title , description ,imgUrl, newsUrl,date ,author, source} = this.props;
    return (
      <div className='my-3'>
             <div className="card">
              <span class="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                    ;sf
                </span>
                <img src={!imgUrl?"https://www.reuters.com/resizer/aI4KXkNUtIeejyKB07Y4HPv4dNo=/1200x628/smart/filters:quality(80)/cloudfront-us-east-2.images.arcpublishing.com/reuters/BJJUFN5JRBI2RKELBTYOI4KUME.jpg":imgUrl} className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{title}...</h5>
                    <p className="card-text">{description}...</p>
                    <p class="card-text"><small class="text-muted">By {!author?"Unknown": author} on: {new Date(date).toGMTString()}</small></p>
                  


                    <a href={newsUrl} target="_blank"className="btn btn-sm btn-dark">Read More</a>
                </div>
        </div>
      </div>
    )
  }
}

export default NewsItems
